var todoApp=angular.module("todoApp",[]);todoApp.controller("todoCtrl",["$scope",function(o){o.tasks=[{text:"brush teeth",done:!1},{text:"comb hair",done:!1},{text:"fold bedsheets",done:!1}],o.todoTotal=function(){var e=0;return o.tasks.forEach(function(o,t,n){o.done===!1&&(e+=1)}),e},o.addTask=function(){""===o.newTask||void 0===o.newTask?console.log("please enter a value"):(o.tasks.push({text:o.newTask,done:!1}),o.newTask="")},o.toggleComplete=function(o){console.log(o.done),o.done=!o.done}}]);